class Product{constructor(id,{name:name,description:description,price:price,soldout:soldout,inventory:inventory,stores:stores}){this.id=id,this.name=name,this.description=description,this.price=price,this.soldout=soldout,this.inventory=inventory,this.stores=stores}}const productDatabase={},resolvers={getProduct:({id:id})=>new Product(id,productDatabase[id]),createProduct:({input:input})=>{let id=require("crypto").randomBytes(10).toString("hex");return productDatabase[id]=input,new Product(id,input)}};export default resolvers;