import{Widgets}from"./dbConnectors";const resolvers={getProduct:async({id:id})=>{try{const product=await Widgets.findById(id);return product}catch(error){throw new Error(error)}},createProduct:async({input:input})=>{const newWidget=new Widgets({name:input.name,description:input.description,price:input.price,soldout:input.soldout,inventory:input.inventory,stores:input.stores});newWidget.id=newWidget._id;try{return await newWidget.save(),newWidget}catch(error){throw new Error(error)}},updateProduct:async({input:input})=>{try{const updateWidget=await Widgets.findOneAndUpdate({_id:input.id},input,{new:!0});return updateWidget}catch(error){throw new Error(error)}}};export default resolvers;