import{buildSchema}from"graphql";const schema=buildSchema("\n    type Product {\n        id: ID\n        name: String\n        description: String\n        price: Float\n        soldout: Boolean\n        stores: [Store]!\n    }\n\n    type Store {\n        store: String\n    }\n\n    type Query {\n        getProduct(id: ID): Product\n    }\n\n    input StoreInput {\n        store: String\n    }\n\n    input ProductInput {\n        id: ID\n        name: String\n        description: String\n        price: Float\n        soldout: Boolean\n        stores: [StoreInput]!\n    }\n    \n\n    type Mutation {\n        createProduct(input: ProductInput): Product\n    }\n");export default schema;