import express from"express";import{graphqlHTTP}from"express-graphql";import schema from"./data/schema";const PORT=8080,app=express();app.get("/",(req,res)=>{res.send("GraphQL is amazing!")});class Product{constructor(id,{name:name,description:description,price:price,soldout:soldout,stores:stores}){this.id=id,this.name=name,this.description=description,this.price=price,this.soldout=soldout,this.stores=stores}}const productDatabase={},root={product:()=>({id:29729872,name:"Widget",description:"Beautiful widget to use in the garden",price:34.99,soldout:!1,stores:[{store:"Pasadena"},{store:"Los Angeles"}]}),createProduct:({input:input})=>{let id=require("crypto").randomBytes(10).toString("hex");return productDatabase[id]=input,new Product(id,input)}};app.use("/graphql",graphqlHTTP({schema:schema,rootValue:root,graphiql:!0})),app.listen(8080,()=>console.log("Running on localhost: 8080/graphql"));