import express from"express";import{graphqlHTTP}from"express-graphql";import schema from"./data/schema";const PORT=8080,app=express();app.get("/",(req,res)=>{res.send("GraphQL is amazing!")});const root={product:()=>({id:29729872,name:"Widget",description:"Beautiful widget to use in the garden",price:34.99,soldout:!1})};app.use("/graphql",graphqlHTTP({schema:schema,rootValue:root,graphiql:!0})),app.listen(8080,()=>console.log("Running on localhost: 8080/graphql"));